import React, { Component } from 'react'
import { Button } from 'react-bootstrap'

export default class Edit extends Component {
    constructor(props) {
      super(props)

      this.state = {
         first:""
      }
    }
  render() {
    return (
      <>
            <td>{this.props.data.id}</td>
            <td>{this.props.data.name}<br/><br/><input type="text" width={"100"} /><Button variant="danger" onClick={()=>this.props.delete(this.props.data.id)}>Delete</Button>&nbsp;&nbsp;<Button variant="success" >Edit Post</Button></td>
            <td><img src={this.props.data.avatar} alt="" /></td>

      </>
    )
  }
}
