import React, { Component } from 'react'
import axios from 'axios'
import { Table, Button } from 'react-bootstrap'
import Update from './update'

export default class Home extends Component {
  constructor(props) {
    super(props)

    this.state = {
      post: [],
      newname: ""
    }
    this.change = this.change.bind(this);
    this.addpost = this.addpost.bind(this);
    this.update = this.update.bind(this);
  }

  change(e) {
    console.log(e.target.value);
    this.setState({ newname: e.target.value });
  }
  addpost() {

    const name = this.state.newname
    console.log("999", name)
    axios.post('https://629dae743dda090f3c07dd7f.mockapi.io/post', { name })
      .then(response => {
        console.log(response.data);
      });
    let a = { name: name }
    console.log("911", a)
    let b = this.state.post.concat(a);
    this.setState({ post: b });
  }
  componentDidMount() {
    axios.get("https://629dae743dda090f3c07dd7f.mockapi.io/post").then((result) => {
      this.setState({ post: result.data });
      console.log(result.data);
    })
  }




  delete(id) {
    axios.delete(`https://629dae743dda090f3c07dd7f.mockapi.io/post/${id}`).then((result) => {
      console.log(result);
    })
    let obj = this.state.post.filter(item => item.id != id)
    this.setState({ post: obj });
  }

  update(id,name){
    const updateName = { name:name }
    console.log(677,updateName)
    axios.put(`https://629dae743dda090f3c07dd7f.mockapi.io/post/${id}`,{ updateName })
    .then((res)=>{
      console.log(777,res.data)
    })
    const obj = this.state.post.find((post)=>post.id == id)
    console.log(7878,obj)
    obj.name = name
    const posts = this.state.post.map((item)=>{
      if(item.id == id){
        return obj
      }
      else{
        return item
      }
    })
    this.setState({post:posts})

  }
  render() {
    return (
      <div className='container'>
        <span style={{ color: "red", fontSize: "20px" }}>Show Details</span>&nbsp;&nbsp;&nbsp;&nbsp;
        <input type={"text"} onChange={this.change} /> &nbsp;&nbsp;
        <Button variant="primary" onClick={this.addpost}>Addpost</Button><br /><br />
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Image</th>
            </tr>
          </thead>
          <tbody>
            {this.state.post.map((item, Index) => {
              return (
                <tr key={Index}>
                  <Update item={item} delete={this.delete.bind(this)} update={this.update}  />
                </tr>
              )
            })}

          </tbody>
        </Table>

      </div>
    )
  }
}
