import React, { Component } from 'react'
import axios from 'axios'
import {Table,Button} from 'react-bootstrap'

export default class Home extends Component {
constructor(props) {
  super(props)

  this.state = {
     post:[],
     string:""
  }
  this.change= this.change.bind(this);
  this.addpost= this.addpost.bind(this);
}

change(e)
{
  this.setState({string:e.target.value});
}
addpost(val)
{
  const article = { title: val };
  axios.post('https://reqres.in/api/articles', article)
      .then(response => this.setState({ articleId: response.data.id }));



}
componentDidMount()
  {
 axios.get("https://629dae743dda090f3c07dd7f.mockapi.io/post").then((result)=>
 {
this.setState({post:result.data});
console.log(result.data);
 })

  }




  delete(id)
  {
    axios.delete(`https://629dae743dda090f3c07dd7f.mockapi.io/post/${id}`).then((result)=>{
    console.log(result);
    })
    let obj = this.state.post.filter( item => item.id != id)
    this.setState({post:obj});
  }
  render() {
    return (
      <div className='container'>
        <input type={"text"} onClick={this.change}/> &nbsp;&nbsp;
        <Button variant="primary" onClick={()=>this.addpost(this.state.string)}>Addpost</Button><br/><br/>
        <Table striped bordered hover>
  <thead>
    <tr>
      <th>ID</th>
      <th>Name</th>
      <th>Image</th>
    </tr>
  </thead>
  <tbody>
    {this.state.post.map((item,index)=>{
      return(
    <tr key={index}>
      <td>{item.id}</td>
      <td>{item.name}<br/><br/><Button variant="danger" onClick={this.delete.bind(this,item.id)}>Delete</Button> </td>
      <td><img src={item.avatar} alt="image" title="Image"/></td>
    </tr>
    )})}

  </tbody>
</Table>

      </div>
    )
  }
}
