import React, { Component } from 'react'
import {Button} from 'react-bootstrap'



export default class Update extends Component {
  constructor(props) {
    super(props)

    this.state = {
       name : ''
    }
  }
  handleChange(e){
    console.log(e.target.value)
     this.setState({name : e.target.value})
  }
  componentDidMount(){
    this.setState({name: this.props.item.name})
  }
  render() {
    return (
      <>    

      <td>{this.props.item.id}</td>
     <td>{this.props.item.name}<br/><br/>

     <input type={"text"} onChange={this.handleChange.bind(this)} value={this.state.name} />&nbsp;&nbsp;&nbsp;
    <Button variant="success" onClick={this.props.update.bind(this,this.props.item.id)}>Update</Button>
    <br />  
    <Button variant="danger" onClick={this.props.delete.bind(this,this.props.item.id)}>Delete</Button></td>
      <td>  <img src={this.props.item.avatar} alt="image" title="Image"/>  </td>

      </>
    )
  }
}
